import React, { useState } from "react";
import { Routes, Route, Link, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import AdminPage from "./pages/AdminPage";
import EditorPage from "./pages/EditorPage";
import ViewerPage from "./pages/ViewerPage";

function App() {
  const [role, setRole] = useState(null); // "Admin" | "Editor" | "Viewer"

  const ProtectedRoute = ({ children, allowedRoles }) => {
    if (!role) return <Navigate to="/login" replace />;
    if (!allowedRoles.includes(role)) return <h2>Access Denied ðŸš«</h2>;
    return children;
  };

  return (
    <div>
      <nav style={{ padding: "10px", background: "#eee" }}>
        <Link to="/">Home</Link> | <Link to="/login">Login</Link> |{" "}
        <Link to="/admin">Admin</Link> | <Link to="/editor">Editor</Link> |{" "}
        <Link to="/viewer">Viewer</Link>
      </nav>

      <Routes>
        <Route path="/" element={<Dashboard role={role} />} />
        <Route path="/login" element={<Login setRole={setRole} />} />

        <Route
          path="/admin"
          element={
            <ProtectedRoute allowedRoles={["Admin"]}>
              <AdminPage />
            </ProtectedRoute>
          }
        />

        <Route
          path="/editor"
          element={
            <ProtectedRoute allowedRoles={["Admin", "Editor"]}>
              <EditorPage />
            </ProtectedRoute>
          }
        />

        <Route
          path="/viewer"
          element={
            <ProtectedRoute allowedRoles={["Admin", "Editor", "Viewer"]}>
              <ViewerPage />
            </ProtectedRoute>
          }
        />
      </Routes>
    </div>
  );
}

export default App;
